package main

import (
	iot "github.com/ctlove0523/huaweicloud-iot-device-sdk-go"
	"strconv"
	"time"
)

const password = "123456789"
const server = "tcp://iot-mqtts.cn-north-4.myhuaweicloud.com:1883"
const produceId = "5fdb75cccbfe2f02ce81d4bf"


func main() {
	content := iot.Message{
		Content: struct {
			msg string
		}{
			msg: "-bBocIt9tA7sV4JmW1KUKMyc2xm6P2J9xWSVhOpB0-pk+69aEz6wAFZtAsKY4IzhAmKBoy0j+29h-oXgm1kZZxn2pNyAaBUDTzdGGChfU-qohQY99gASdDerT5awO1SPC22b6hBnQ5DSdr+IUfj8dmKwSkJusJsaEufmBeUmASgFJPpKWIkX2VTCMa9iwuTkr+K+t8VMGqzYIgRnnEeHdwZw8E83ZlaPaZxHYJDf6DGGtjZs8gkBsG0HQ+3KTfS-FRlHpP5pPOY6JX1LAtKj5YdwXksO3f7GwyVmXqrN+Y",
		},
	}
	for i := 1; i <= 110; i++ {
		go SendMessage(strconv.Itoa(i), content)
	}

	time.Sleep(time.Hour)
}

func SendMessage(id string, message iot.Message) {
	device := iot.CreateIotDevice(produceId+"_test-"+id, password, server)
	device.Init()

	for i := 0; i < 100; i++ {
		device.SendMessage(message)
	}

	time.Sleep(time.Hour)

}
